    import qdarktheme
    app = QtWidgets.QApplication(sys.argv)
    qdarktheme.setup_theme()
    MainWindow = QtWidgets.QMainWindow()
    ui = Ui_MainWindow(searcher)
    ui.setupUi(MainWindow)
    MainWindow.show()
    sys.exit(app.exec_())
